import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { useEffect } from 'react';
import axios from 'axios';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
		paper: {
			backgroundColor: theme.palette.background.paper,
			border: '2px solid #000',
			boxShadow: theme.shadows[5],
			padding: theme.spacing(2, 4, 3),
		},
	}),
);

export const AddUser = ({ newUser }) => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};
	const [file, setFile] = React.useState('');
	const handleChange = (event) => {
		setFile(URL.createObjectURL(event.target.files[0]));
	};
	const [user, setUser] = React.useState({
		name: '',
		phone: '',
		email: '',
		role: '',
	});

	const onChange = (event) => {
		const name = event.target.name;
		const value = event.target.value;
		setUser({ ...user, [name]: value });
	};
	const onSubmit = (event) => {
		event.preventDefault();
		handleClose();
		axios({
			method: 'POST',
			url: 'https://60517468534609001767198c.mockapi.io/api/user',
			data: null,
		})
			.then((res) => {
				console.log(res.data.name);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	return (
		<div>
			<button type='button' onClick={handleOpen}>
				Thêm User
			</button>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className={classes.paper}>
						<div className=' form-modal'>
							<div className='form-modal__title'>
								<h3>Thêm Người Dùng Mới</h3>
							</div>
							<div className='PacketNew_content form-modal__content'>
								<div className='PacketNew_note note'>
									<p>Gửi thông báo đến người dùng</p>
									<div className=''>
										<span>
											<Checkbox inputProps={{ 'aria-label': 'uncontrolled-checkbox' }} />
										</span>
										<span>Gửi cho người dùng mới đăng ký 1 email chứa thông tin tài khoản của họ</span>
									</div>
								</div>
								<form className='PacketNew__form form ' onSubmit={onSubmit}>
									<div className='PacketNew__form--file form_file'>
										<h3>Thông tin cơ bản</h3>
										<div className=''>
											<input type='file' onChange={handleChange} className='upload' />

											{!!file && <img src={file} alt='preview' />}
										</div>
										<TextField id='standard-name' label='Tên đăng nhập' name='name' value={user.name} onChange={onChange} />
										<TextField id='standard-name' label='Số điện thoại' name='phone' value={user.phone} onChange={onChange} />
										<TextField id='standard-name' label='Email' name='email' value={user.email} onChange={onChange} />
										<TextField id='standard-name' label='Vai trò' name='role' value={user.role} onChange={onChange} />
									</div>
									<div className='PacketNew__form--button form_button'>
										<Button variant='outlined'>Hủy bỏ</Button>
										<Button
											variant='outlined'
											type='submit'
											value='Submit'
											onClick={() => {
												newUser(user);
											}}
										>
											Tạo mới
										</Button>
									</div>
								</form>
							</div>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
