import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Input from '@material-ui/core/Input';
import Button from '@material-ui/core/Button';
import InputAdornment from '@material-ui/core/InputAdornment';
import './style.scss';

const useStyles = makeStyles((theme) => ({
	modal: {
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'center',
	},
	paper: {
		backgroundColor: theme.palette.background.paper,
		border: '2px solid #000',
		boxShadow: theme.shadows[5],
		padding: theme.spacing(2, 4, 3),
	},
	margin: {
		margin: theme.spacing(1),
	},
	input: {
		display: 'none',
	},
}));

export const PacketNew = () => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};

	return (
		<div className='PacketNew_button'>
			<button type='button' onClick={handleOpen}>
				Tạo gói tin
			</button>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className={classes.paper}>
						<div className='PacketNew_wrapper'>
							<div className='title'>
								<h3>TẠO MỚI GÓI TIN</h3>
							</div>
							<div className='PacketNew_content'>
								<div className='PacketNew_note'>
									<p>Gói Tin được áp dụng với:</p>
									<p>1. Chuyên mục có phí</p>
									<p>2. Tin đăng về Cần bán/ Cho thuê</p>
									<p>3. Không áp dụng đối với banner quảng cáo, quảng cáo cơ bản</p>
								</div>
								<div className='PacketNew__form'>
									<div className='PacketNew__form--file'>
										<FormControl className={classes.margin}>
											<InputLabel htmlFor='input-with-icon-adornment'>Mã gói</InputLabel>
											<Input id='input-with-icon-adornment' startAdornment={<InputAdornment position='start'></InputAdornment>} />
										</FormControl>
										<FormControl className={classes.margin}>
											<InputLabel htmlFor='input-with-icon-adornment'>Tên gói</InputLabel>
											<Input id='input-with-icon-adornment' startAdornment={<InputAdornment position='start'></InputAdornment>} />
										</FormControl>
										<FormControl className={classes.margin}>
											<InputLabel htmlFor='input-with-icon-adornment'>Phí</InputLabel>
											<Input id='input-with-icon-adornment' startAdornment={<InputAdornment position='start'></InputAdornment>} />
										</FormControl>
										<FormControl className={classes.margin}>
											<InputLabel htmlFor='input-with-icon-adornment'>Chiết khấu</InputLabel>
											<Input id='input-with-icon-adornment' startAdornment={<InputAdornment position='start'></InputAdornment>} />
										</FormControl>
										<FormControl className={classes.margin}>
											<InputLabel htmlFor='input-with-icon-adornment'>Số lượng tin</InputLabel>
											<Input id='input-with-icon-adornment' startAdornment={<InputAdornment position='start'></InputAdornment>} />
										</FormControl>
										<FormControl className={classes.margin}>
											<InputLabel htmlFor='input-with-icon-adornment'>Thời hạn sử dụng</InputLabel>
											<Input id='input-with-icon-adornment' startAdornment={<InputAdornment position='start'></InputAdornment>} />
										</FormControl>
									</div>
									<div className='PacketNew__form--button'>
										<Button variant='outlined'>Hủy bỏ</Button>
										<Button variant='outlined'>Tạo mới</Button>
									</div>
								</div>
							</div>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
