import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import '../../../../pages/reset.scss';
import VisibilityIcon from '@material-ui/icons/Visibility';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
	}),
);

export const InfoCampaignList = () => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};
	return (
		<div>
			<div onClick={handleOpen}>
				<VisibilityIcon />
			</div>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className=' form-modal informationStore'>
						<div className='form-modal__title'>
							<h3>CHI TIẾT CHIẾN DỊCH</h3>
						</div>
						<div className='form-modal__content'>
							<div className='CampaignList-code'>
								<span>Mã chiến dịch:</span>
								<span>
									<strong>CD_01</strong>
								</span>
							</div>
							<div className='InfoCampaignList__item'>
								<div className='content'>
									<div className='content__item'>
										<label htmlFor=''>Tên chiến dịch</label>
										<input className='form-input' placeholder='nhập số điện thoại'></input>
									</div>
									<div className='content__item'>
										<label htmlFor=''>Loại quảng cáo</label>
										<input className='form-input' placeholder='nhập email'></input>
									</div>
									<div className='content__item'>
										<label htmlFor=''>Khu vực hiển thị</label>
										<input className='form-input' placeholder='nhập website'></input>
									</div>
									<div className='content__item'>
										<label htmlFor=''>Danh mục</label>
										<input className='form-input' placeholder='nhập địa chỉ'></input>
									</div>
									<div className='content__item'>
										<label htmlFor=''>Link quảng cáo</label>
										<input className='form-input' placeholder='nhập địa chỉ'></input>
									</div>
									<div className='content__item'>
										<label htmlFor=''>Mô tả ngắn</label>
										<textarea className='form-input' placeholder='nhập địa chỉ'></textarea>
									</div>
								</div>
							</div>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
