import { AllTypeActions } from '../types/index';

export const getUsers = () => {
	return {
		type: AllTypeActions.LOAD_USERS_LOADING,
	};
};

export const addUser = (input: any) => {
	return {
		type: AllTypeActions.ADD_USER,
		input,
	};
};

export const deleteUser = (input: any) => {
	return {
		type: AllTypeActions.DELETE_USER,
		input,
	};
};
export const checkUser = (checked: boolean, index: number) => {
	return {
		type: AllTypeActions.CHECKBOX_USER,
		index,
		checked,
	};
};

export const checkAllUser = (checked: boolean) => {
	return {
		type: AllTypeActions.CHECKBOX_ALL_USER,
		checked,
	};
};
