import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import 'rsuite/dist/styles/rsuite-default.css';
import '../../../pages/reset.scss';
import { Sidebar } from '../../../components';
import { MenuPopupState } from '../../../components/Account/AccountButton';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import '../../../pages/reset.scss';
import 'react-super-responsive-table/dist/SuperResponsiveTableStyle.css';
import { SeeDetails } from './seeDetails';
import DeleteIcon from '@material-ui/icons/Delete';
import { Post } from './post';

export const PostingList = () => {
	const colCheckboxes = document.querySelectorAll('.col-checkbox');
	const onChange = (event) => {
		colCheckboxes.forEach((element) => {
			element.checked = false;
			const checked = event.target.checked;
			const colName = element.getAttribute('data-col');
			const cells = document.querySelectorAll(`.${colName}`);
			console.log('checked', checked);
			hideShowTableCol(cells, checked);
		});
	};
	const hideShowTableCol = (cells, checked) => {
		cells.forEach((cell) => {
			console.log('cell', cell);
			cell.style.display = checked === true ? 'table-cell' : 'none';
		});
	};

	return (
		<div className='page-wrapper'>
			<div className='page-left'>
				<div className='page_sidebar'>
					<Sidebar />
				</div>
			</div>
			<div className='page-right'>
				<div className='page_menu'>
					<MenuPopupState />
				</div>
				<div className='tab__wrapper posting-List '>
					<div className='Breadcrumb'>
						<span>Cấu Hình</span>
						<span>
							<ArrowForwardIosIcon />
						</span>
						<span>Sao Chép Tin Đăng</span>
					</div>
					<div className='Account tab__wrapper'>
						<div className='Account__title tab___content'>
							<Button>Danh Sách Đăng Ký</Button>
						</div>
					</div>
					<div className='TableList table-wrapper'>
						<div className='search '>
							<input type='text' placeholder='Nhập link tin đăng tại đây...' />
							<button>Lấy Thông tin</button>
						</div>
						<div className='TableList__button'>
							<div className=''>
								<button className='button_danger'>100 Tin</button>
								<button className='button_save'>Chờ duyệt</button>
								<button className='button_save'>Đã duyệt</button>
								<input type='text' className='form-input' placeholder='Tìm người duyệt tại đây....' />
							</div>
							<div className='action'>
								<div className='icon'>
									<DeleteIcon />
								</div>
								<div className='icon'>
									<Post />
								</div>
							</div>
						</div>
						<div className='TableList__content TableUser'>
							<div className='TableUser__list TableUser__list'>
								<ul id='columns-dropdown'>
									<li>
										<input type='checkbox' data-col='col-name' className='col-checkbox' onChange={onChange} />
										Name
									</li>
									<li>
										<input type='checkbox' data-col='col-last-name' className='col-checkbox' onChange={onChange} />
										Last Name
									</li>
									<li>
										<input type='checkbox' data-col='col-phone-number' className='col-checkbox' onChange={onChange} />
										Phone Number
									</li>
									<li>
										<input type='checkbox' data-col='col-city' className='col-checkbox' onChange={onChange} />
										City
									</li>
								</ul>

								<table>
									<thead>
										<th className='col-name'>Name</th>
										<th className='col-last-name'>Last Name</th>
										<th className='col-phone-number'>Phone Number</th>
										<th className='col-city'>City</th>
									</thead>
									<tbody>
										<tr>
											<td className='col-name'>David</td>
											<td className='col-last-name'>Martin</td>
											<td className='col-phone-number'>44-5353254</td>
											<td className='col-city'>Tandil</td>
										</tr>
										<tr>
											<td className='col-name'>Jorge</td>
											<td className='col-last-name'>Pérez</td>
											<td className='col-phone-number'>44-5353254</td>
											<td className='col-city'>Tandil</td>
										</tr>
									</tbody>
								</table>
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};
