import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import '../../../../pages/reset.scss';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Popper from '@material-ui/core/Popper';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
		paper: {
			border: '1px solid',
			padding: theme.spacing(1),
			backgroundColor: theme.palette.background.paper,
		},
	}),
);

export const AddAdvertisementlist = (props) => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);
	const handleOpen = () => {
		setOpen(true);
	};
	const handleClose = () => {
		setOpen(false);
	};
	const [tags, setTags] = React.useState(props.tags);
	const removeTags = (indexToRemove) => {
		setTags([...tags.filter((_, index) => index !== indexToRemove)]);
	};
	const addTags = (event) => {
		if (event.target.value !== '') {
			setTags([...tags, event.target.value]);
			props.selectedTags([...tags, event.target.value]);
			event.target.value = '';
		}
	};

	return (
		<div>
			<div onClick={handleOpen}>
				<button className='button_add'>Thêm Loại Quảng Cáo</button>
			</div>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className=' form-modal informationStore'>
						<div className='form-modal__title'>
							<h3>TẠO LOẠI QUẢNG CÁO MỚI</h3>
						</div>
						<div className='form-modal__content'>
							<div className='CampaignList-code'>
								<span>Mã Loai</span>
								<span>
									<strong>CD_01</strong>
								</span>
							</div>
						</div>
						<div className='form'>
							<div className='form-file'>
								<div className='AddAdvertisementlist__item'>
									<label htmlFor=''></label>
									<input type='text' placeholder='Nhập nội dung tại đây...' />
