													<div className='item'>
														<span className='form-input'>Danh sách tìm kiếm</span>
														<span className='form-input'></span>
													</div>
													<div className='add__item'>
														<div onClick={handleOpen}>
															<button>Thêm Loại Quảng Cáo</button>
