import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import '../../../../pages/reset.scss';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Popper from '@material-ui/core/Popper';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
	}),
);

export const AddAdvertisementlist = () => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};
	const id = open ? 'simple-popper' : undefined;
	return (
		<div>
			<div onClick={handleOpen}>
				<button className='button_add'>Thêm Loại Quảng Cáo</button>
			</div>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className=' form-modal informationStore'>
						<div className='form-modal__title'>
							<h3>TẠO LOẠI QUẢNG CÁO MỚI</h3>
						</div>
						<div className='form-modal__content'>
							<div className='CampaignList-code'>
								<span>Mã Loai</span>
								<span>
									<strong>CD_01</strong>
								</span>
							</div>
						</div>
						<div className='form'>
							<div className='form-file'>
								<div className='AddAdvertisementlist__item'>
									<label htmlFor=''></label>
									<input type='text' placeholder='Nhập nội dung tại đây...' />
								</div>
								<div className='AddAdvertisementlist'>
									<div className='AddAdvertisementlist__left'>
										<div>
											<Accordion>
												<AccordionSummary expandIcon={<ExpandMoreIcon />}>
													<span>Máy tính</span>
												</AccordionSummary>
												<AccordionDetails>
													<div className='item'>
														<input />
														<input />
													</div>
													<div className='add__item'>
														<button type='button' onClick={handleOpen}>
															react-transition-group
														</button>
														<Modal
															aria-labelledby='transition-modal-title'
															aria-describedby='transition-modal-description'
															className={classes.modal}
															open={open}
															onClose={handleClose}
															closeAfterTransition
															BackdropComponent={Backdrop}
															BackdropProps={{
																timeout: 500,
															}}
														>
															<Fade in={open}>
																<div className={classes.paper}>
																	<h2 id='transition-modal-title'>Transition modal</h2>
																	<p id='transition-modal-description'>react-transition-group animates me.</p>
																</div>
															</Fade>
														</Modal>
													</div>
												</AccordionDetails>
											</Accordion>
											<Accordion>
												<AccordionSummary expandIcon={<ExpandMoreIcon />}>
													<Typography>Accordion 2</Typography>
												</AccordionSummary>
												<AccordionDetails></AccordionDetails>
											</Accordion>
											<Accordion>
												<AccordionSummary expandIcon={<ExpandMoreIcon />}>
													<Typography>Accordion 2</Typography>
												</AccordionSummary>
												<AccordionDetails></AccordionDetails>
											</Accordion>
											<Accordion>
												<AccordionSummary expandIcon={<ExpandMoreIcon />}>
													<Typography>Accordion 2</Typography>
												</AccordionSummary>
												<AccordionDetails></AccordionDetails>
											</Accordion>
										</div>
									</div>
									<div className='AddAdvertisementlist__right'></div>
								</div>
							</div>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
