import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import '../../../../pages/reset.scss';
import Button from '@material-ui/core/Button';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import DeleteIcon from '@material-ui/icons/Delete';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
	}),
);

export const AdvertisingPosition = (props) => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};
	const [tags, setTags] = React.useState(props.tags);
	const removeTags = (indexToRemove) => {
		setTags([...tags.filter((_, index) => index !== indexToRemove)]);
	};
	const addTags = (event) => {
		if (event.target.value !== '') {
			setTags([...tags, event.target.value]);
			props.selectedTags([...tags, event.target.value]);
			event.target.value = '';
		}
		return tags;
	};

	return (
		<div>
			<div onClick={handleOpen}>
				<button className='button_add AdvertisingPosition-button'>Vị trí Quảng cáo</button>
			</div>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className=' form-modal informationStore'>
						<div className='form-modal__title'>
							<h3>Vị trí Quảng cáo</h3>
						</div>
						<div className='form-modal__content'>
							<div className='form'>
								<div className='form_file'>
									<Accordion className='item'>
										<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '></AccordionSummary>
										<AccordionDetails>
											<ul id='tags'>
												{tags.map((tag, index) => (
													<li key={index} className='tag'>
														<span className='tag-title form-input'>{tag}</span>
														<span className='tag-close-icon' onClick={() => removeTags(index)}>
															<DeleteIcon />
														</span>
													</li>
												))}
											</ul>
										</AccordionDetails>
									</Accordion>
									<input type='text' onKeyUp={(event) => (event.key === 'Enter' ? addTags(event) : null)} placeholder='Máy tính' />
								</div>
							</div>
							<div className='form_button'>
								<Button variant='outlined'>Đóng</Button>
							</div>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
