import { useEffect } from 'react';
import { AddUser } from './AddUser';
import Checkbox from '@material-ui/core/Checkbox';
import DeleteIcon from '@material-ui/icons/Delete';
import './style.scss';
import VisibilityIcon from '@material-ui/icons/Visibility';
import * as React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getUsers } from '../../../../store/actions/user';
import { IApplicationState } from '../../../../store/reducers/state';
import { addUser } from '../../../../store/actions/user';

export const UserList = () => {
	const dispatch = useDispatch();
	const users = useSelector((state: IApplicationState) => {
		return state.getUsers?.data;
	});
	useEffect(() => {
		dispatch(getUsers());
	}, [dispatch]);
	const newUser = (user) => {
		dispatch(addUser(user));
	};
	const [userdata, setUserData] = React.useState(
		useSelector((state: IApplicationState) => {
			return state.getUsers?.data;
		}),
	);
	console.log(userdata);
	// const onChange = (event) => {
	// 	const id = event.target.id;
	// 	setUserData(() => {
	// 		return {
	// 			userdata: userdata.map((item) => (item.id === +id ? { ...item, Checked: !item.Checked } : item)),
	// 		};
	// 	});
	// };
	return (
		<div className='User-List'>
			<div className='Add-user'>
				<AddUser newUser={newUser} />
			</div>
			<div className='table-user table-wrapper'>
				<div className='TableUser'>
					<div className='TableUser__search'>
						<button className='button_blue'>xóa user</button>
						<input placeholder='Tìm kiếm tại đây...'></input>
					</div>
					<div className='TableUser__list'>
						<table>
							<thead>
								<tr>
									<th>
										<Checkbox inputProps={{ 'aria-label': 'uncontrolled-checkbox' }} />
									</th>
									<th>User ID</th>
									<th>Tên đăng nhập</th>
									<th>Số điện thoại</th>
									<th>Email</th>
									<th>Vai trò</th>
									<th>Thao tác</th>
								</tr>
							</thead>
							<tbody>
								{users.map((item: any, index: number) => {
									return (
										<tr>
											<td>
												{/* <Checkbox inputProps={{ 'aria-label': 'uncontrolled-checkbox' }} checked={item.checked} /> */}
												<input type='checkbox' name='newsletter' value='Hourly' checked={item.Checked}></input>
											</td>
											<td>{index}</td>
											<td>{item.name}</td>
											<td>{item.email}</td>
											<td>{item.role}</td>
											<td>{item.phone}</td>
											<td>
												<div className='action'>
													<VisibilityIcon />
													<DeleteIcon />
												</div>
											</td>
										</tr>
									);
								})}
							</tbody>
						</table>
					</div>
				</div>
			</div>
		</div>
	);
};
