import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import AddBoxIcon from '@material-ui/icons/AddBox';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
	}),
);

export const QuicklyCategory = () => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};
	const [tags, setTags]: any = React.useState([]);
	const removeTags = (indexToRemove) => {
		setTags([...tags.filter((_, index) => index !== indexToRemove)]);
	};
	const addTags = (event) => {
		if (event.target.value !== '') {
			setTags([...tags, event.target.value]);
			event.target.value = '';
		}
	};

	return (
		<div>
			<div onClick={handleOpen}>
				<button className='add'>
					<AddBoxIcon />
				</button>
			</div>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className=' form-modal informationStore  advertisement AdvertisingPosition'>
						<div className='form-modal__title'>
							<h3>Tạo Nhanh Danh Mục</h3>
						</div>
						<div className='form-modal__content'>
							<div className='form'>
								<div className='form_file'>
									<div className='tags-input'>
										<ul id='tags'>
											{tags.map((tag, index) => (
												<li key={index} className='tag '>
													<span className='tag-title '>{tag}</span>
													<span className='tag-close-icon' onClick={() => removeTags(index)}>
														x
													</span>
												</li>
											))}
										</ul>
										<textarea className='form-input' onKeyUp={(event) => (event.key === 'Enter' ? addTags(event) : null)} placeholder='Nhập vị trí tại đây ...'></textarea>
										<button className='add-tag'>Taọ mới</button>
									</div>
								</div>
							</div>
							<div className='form_button'>
								<Button variant='outlined' onClick={handleClose}>
									Đóng
								</Button>
							</div>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
