import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
	}),
);

export const NewCategory = () => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};
	return (
		<div>
			<div onClick={handleOpen}>
				<button className='button_add '>THÊM MỚI</button>
			</div>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className=' form-modal informationStore  advertisement AdvertisingPosition'>
						<div className='form-modal__title'>
							<h3>Thêm Mới Danh Mục</h3>
						</div>
						<div className='form-modal__content'>
							<div className='form'>
								<div className='form_file'>
									<div className='new-categoty'>
										<div className=''>
											<div className=''>
												<label htmlFor=''>Tiêu đề</label>
												<input type='text' className='form-input' />
											</div>
											<div className=''>
												<label htmlFor=''>Danh mục cha</label>
												<Accordion className=' '>
													<AccordionSummary className=' ' expandIcon={<ExpandMoreIcon />}>
														Thời trang
													</AccordionSummary>
													<AccordionDetails className=''>Quần áo</AccordionDetails>
													<AccordionDetails className=''>Đồng hồ</AccordionDetails>
												</Accordion>
											</div>
											<div className=''>
												<label htmlFor=''>Ảnh</label>
											</div>
										</div>
									</div>
								</div>
							</div>
							<div className='form_button'>
								<Button variant='outlined' onClick={handleClose}>
									Đóng
								</Button>
							</div>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
