import React from 'react';
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import '../../../../pages/reset.scss';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import DeleteIcon from '@material-ui/icons/Delete';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme: Theme) =>
	createStyles({
		modal: {
			display: 'flex',
			alignItems: 'center',
			justifyContent: 'center',
		},
		paper: {
			border: '1px solid',
			padding: theme.spacing(1),
			backgroundColor: theme.palette.background.paper,
		},
	}),
);

export const TagsInput = (props) => {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);
	const handleOpen = () => {
		setOpen(true);
	};
	const handleClose = () => {
		setOpen(false);
	};
	const [tags, setTags] = React.useState(props.tags);
	const removeTags = (indexToRemove) => {
		setTags([...tags.filter((_, index) => index !== indexToRemove)]);
	};
	const addTags = (event) => {
		if (event.target.value !== '') {
			setTags([...tags, event.target.value]);
			props.selectedTags([...tags, event.target.value]);
			event.target.value = '';
		}
		return tags;
	};

	const [mobile, setMobile] = React.useState(props.mobile);
	const removeTag = (indexToRemove) => {
		setMobile([...mobile.filter((_, index) => index !== indexToRemove)]);
	};
	const addTag = (event) => {
		if (event.target.value !== '') {
			setMobile([...mobile, event.target.value]);
			props.selectedTag([...mobile, event.target.value]);
			event.target.value = '';
		}
		return tags;
	};
	const [AddAdvertisementlist, setAddAdvertisementlist] = React.useState({
		id: '',
		computer: tags,
		webMobile: mobile,
		fee: '',
		discount: '',
		date: '',
		note: '',
		area: [],
		category: {},
		typeAdvertisementlist: '',
	});
	const onChange = (event) => {
		const name = event.target.name;
		const value = event.target.value;
		setAddAdvertisementlist({ ...AddAdvertisementlist, [name]: value });
	};
	const onSubmit = (event) => {
		event.preventDefault();
		handleClose();
	};
	console.log(AddAdvertisementlist);

	return (
		<div>
			<div onClick={handleOpen}>
				<button className='button_add'>Thm Loi Qung Co</button>
			</div>
			<Modal
				aria-labelledby='transition-modal-title'
				aria-describedby='transition-modal-description'
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className=' form-modal informationStore'>
						<div className='form-modal__title'>
							<h3>TO LOI QUNG CO MI</h3>
						</div>
						<div className='form-modal__content'>
							<div className='CampaignList-code'>
								<span>M Loai</span>
								<span>
									<strong>CD_01</strong>
								</span>
							</div>
							<form className='form' onSubmit={onSubmit}>
								<div className='form_file'>
									<div className='AddAdvertisementlist__input'>
										<label htmlFor=''>Loi qung co:</label>
										<input
											className='form-input'
											type='text'
											placeholder='Nhp ni dung ti y...'
											name='typeAdvertisementlist'
											value={AddAdvertisementlist.typeAdvertisementlist}
											onChange={onChange}
										/>
									</div>
									<div className='AddAdvertisementlist'>
										<div className='AddAdvertisementlist__left'>
											<div className='AddAdvertisementlist__item'>
												<label htmlFor=''>V tr</label>
												<Accordion className='item'>
													<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
														<input type='text' onKeyUp={(event) => (event.key === 'Enter' ? addTags(event) : null)} placeholder='My tnh' />
													</AccordionSummary>
													<AccordionDetails>
														<ul id='tags'>
															{tags.map((tag, index) => (
																<li key={index} className='tag'>
																	<span className='tag-title form-input'>{tag}</span>
																	<span className='tag-close-icon' onClick={() => removeTags(index)}>
																		<DeleteIcon />
																	</span>
																</li>
															))}
														</ul>
													</AccordionDetails>
												</Accordion>
												<Accordion className='item'>
													<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
														<input type='text' onKeyUp={(event) => (event.key === 'Enter' ? addTag(event) : null)} placeholder='Web Mobile' />
													</AccordionSummary>
													<AccordionDetails>
														<ul>
															{mobile.map((mobile, index) => (
																<li key={index} className='tag'>
																	<span className='tag-title form-input'>{mobile}</span>
																	<span className='tag-close-icon' onClick={() => removeTag(index)}>
																		<DeleteIcon />
																	</span>
																</li>
															))}
														</ul>
													</AccordionDetails>
												</Accordion>
												<Accordion className='item'>
													<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
														<input type='text' onKeyUp={(event) => (event.key === 'Enter' ? addTags(event) : null)} placeholder='ng dng' />
													</AccordionSummary>
													<AccordionDetails></AccordionDetails>
												</Accordion>
												<Accordion className='item'>
													<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
														<input type='text' onKeyUp={(event) => (event.key === 'Enter' ? addTags(event) : null)} placeholder='Qung co c bn' />
													</AccordionSummary>
													<AccordionDetails></AccordionDetails>
												</Accordion>
											</div>
											<div className='AddAdvertisementlist__item'>
												<label htmlFor=''>Ph</label>
												<input type='text' className='form-input' name='fee' value={AddAdvertisementlist.fee} onChange={onChange} />
											</div>
											<div className='AddAdvertisementlist__item'>
												<label htmlFor=''>Chit khu</label>
												<input type='text' className='form-input' name='discount' value={AddAdvertisementlist.discount} onChange={onChange} />
											</div>
											<div className='AddAdvertisementlist__item'>
												<label htmlFor=''>Thi hn s dng</label>
												<input type='text' className='form-input' name='date' value={AddAdvertisementlist.date} onChange={onChange} />
											</div>
											<div className='AddAdvertisementlist__item'>
												<label htmlFor=''>Ghi ch</label>
												<textarea name='' id='' className='form-input' value={AddAdvertisementlist.note} onChange={onChange}></textarea>
											</div>
										</div>
										<div className='AddAdvertisementlist__right'>
											<div className='AddAdvertisementlist__item'>
												<label htmlFor=''>Khu vc:</label>
												<Accordion className='item'>
													<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
														<input type='text' onKeyUp={(event) => (event.key === 'Enter' ? addTags(event) : null)} placeholder='Ton quc' />
													</AccordionSummary>
													<AccordionDetails></AccordionDetails>
												</Accordion>
											</div>
											<div className='AddAdvertisementlist__item'>
												<label htmlFor=''>Danh mc</label>
												<Accordion className='item'>
													<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
														<input type='text' placeholder='Chn danh mc' disabled />
													</AccordionSummary>
													<AccordionDetails>
														<Accordion className='item'>
															<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
																<input type='text' placeholder='Thi trang' disabled />
															</AccordionSummary>
															<AccordionDetails>
																<div className=''>
																	<span>
																		<Checkbox />
																	</span>
																	<span>Qun o</span>
																</div>
																<div className=''>
																	<span>
																		<Checkbox />
																	</span>
																	<span>ng h</span>
																</div>
															</AccordionDetails>
														</Accordion>
														<Accordion className='item'>
															<AccordionSummary expandIcon={<ExpandMoreIcon />} className='item-title '>
																<input type='text' placeholder=' in t' disabled />
															</AccordionSummary>
															<AccordionDetails>
																<div className=''>
																	<span>
																		<Checkbox />
																	</span>
																	<span>Qun o</span>
																</div>
																<div className=''>
																	<span>
																		<Checkbox />
																	</span>
																	<span>ng h</span>
																</div>
															</AccordionDetails>
														</Accordion>
													</AccordionDetails>
												</Accordion>
											</div>
										</div>
									</div>
								</div>
								<div className='form_button'>
									<Button variant='outlined'>Kch hot</Button>
									<Button
										variant='outlined'
										// type='submit'
										onClick={() => {
											TagsInput(props);
										}}
									>
										Hy b
									</Button>
								</div>
							</form>
						</div>
					</div>
				</Fade>
			</Modal>
		</div>
	);
};
