import { AnyAction, Reducer } from 'redux';
// import { IAppRequestState, initialRequestState, GlobalReducer } from './state';
import { AllTypeActions } from '../types';

// export const FilterProductReducer: Reducer = (state: IAppRequestState = initialRequestState, action: AnyAction) => {
// 	return GlobalReducer(state, action, AllTypeActions.FILTER_PRODUCT.toString());
// };
export const FilterProductReducer: Reducer = (state: { data: any; loading: boolean; error: string } = { data: [], loading: false, error: '' }, action: AnyAction) => {
	switch (action.type) {
		case AllTypeActions.FILTER_PRODUCT: {
			return {
				...state,
				loading: true,
				error: '',
			};
		}
		case AllTypeActions.LOAD_USERS_SUCCESS: {
			return {
				...state,
				data: action.data,
				loading: false,
			};
		}
		case AllTypeActions.LOAD_USERS_ERROR: {
			return {
				...state,
				loading: false,
				error: action.error,
			};
		}
	}
};
