import axios from 'axios';
import { objectToFormData } from '../../helpers';

const defaultHeader = (token?: string | null) => {
	const header: any = {
		'Content-Type': 'application/json',
		'Cache-Control': 'no-cache',
	};
	if (token) header.Authorization = `Bearer ${token}`;
	return header;
};

const defaultUser = (token?: string | null) => {
	const user: any = {
		'Content-Type': 'application/json',
		'Cache-Control': 'no-cache',
	};
	if (token) user.Authorization = `Bearer ${token}`;
	return user;
};

class ApiRequests {
	public get(apiUrl: string, params: any = {}, isAuth: boolean = true): Promise<any> {
		const headers = defaultHeader(isAuth ? localStorage.getItem('backoffice_token') : null);
		const users = defaultUser(isAuth ? localStorage.getItem('backoffice_token') : null);
		return new Promise<any>((resolve, reject) => {
			axios
				.get(apiUrl, { headers, params })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});

			axios
				.get(apiUrl, { headers, params })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});
		});
	}

	public post(apiUrl: string, input: any = {}, isAuth: boolean = true, jsonParser: boolean = false): Promise<any> {
		const data = jsonParser ? JSON.stringify(input) : objectToFormData(input);
		const headers = defaultHeader(isAuth ? localStorage.getItem('backoffice_token') : null);
		const users = defaultUser(isAuth ? localStorage.getItem('backoffice_token') : null);
		return new Promise<any>((resolve, reject) => {
			axios
				.post(apiUrl, data, { headers })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});

			axios
				.post(apiUrl, data, { users })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});
		});
	}

	public put(apiUrl: string, input: any = {}, isAuth: boolean = true): Promise<any> {
		const data = objectToFormData(input);
		const headers = defaultHeader(isAuth ? localStorage.getItem('backoffice_token') : null);
		const users = defaultUser(isAuth ? localStorage.getItem('backoffice_token') : null);
		return new Promise<any>((resolve, reject) => {
			axios
				.put(apiUrl, data, { headers })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});

			axios
				.put(apiUrl, data, { users })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});
		});
	}

	public delete(apiUrl: string, params: any = {}, isAuth: boolean = true): Promise<any> {
		const headers = defaultHeader(isAuth ? localStorage.getItem('backoffice_token') : null);
		const users = defaultUser(isAuth ? localStorage.getItem('backoffice_token') : null);
		return new Promise<any>((resolve, reject) => {
			axios
				.delete(apiUrl, { headers, params })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});

			axios
				.delete(apiUrl, { users, params })
				.then((response) => {
					const jsonData = response.data;
					resolve(jsonData);
				})
				.catch((e) => {
					reject(e);
				});
		});
	}
}

export const Requests = new ApiRequests();
