import React from 'react';
import MaterialTable, { Column } from 'material-table';
import { Sidebar } from '../../../components';
import { MenuPopupState } from '../../../components/Account/AccountButton';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';

export const ContentPage = () => {
	const [state, setState]: [{ columns: Column<any>[]; data: any[] }, any] = React.useState({
		columns: [
			{ title: 'STT', field: 'order', type: 'numeric' },
			{ title: 'Mã gói', field: 'code', type: 'string' },
			{ title: 'Tên gói', field: 'surname', type: 'string' },
			{ title: 'Số lượng tin', field: 'number', type: 'numeric' },
			{ title: 'Phí', field: 'fees', type: 'numeric' },
			{ title: 'Chiết khấu', field: 'discount', type: 'numeric' },
			{ title: 'Thành tiền', field: 'money', type: 'numeric' },
			{
				title: 'Thời hạn sử dụng',
				field: 'date',
				type: 'string',
			},
		],
		data: [
			{ order: '1', code: 'MG_01', surname: 'Gói 5 Tin Đăng', number: 5, fees: 100, discount: 10, money: 200, date: '2 tháng' },
			{
				order: '2',
				code: 'MG_02',
				surname: 'Gói 50 Tin Đăng',
				number: 20,
				fees: 100,
				discount: 10,
				money: 200,
				birthCity: 34,
				date: '2 tháng',
			},
		],
	});
	return (
		<div className='page-wrapper'>
			<div className='page-left'>
				<div className='page_sidebar'>
					<Sidebar />
				</div>
			</div>
			<div className='page-right'>
				<div className='page_menu'>
					<MenuPopupState />
				</div>
				<div className='tab__wrapper configuration'>
					<div className='Breadcrumb'>
						<span>
							<span>Cấu Hình</span>
							<ArrowForwardIosIcon />
							<strong>Trang Nội Dung</strong>
						</span>
					</div>
					<div className='TableList table-wrapper'>
						<MaterialTable
							title='Editable Example'
							columns={state.columns}
							data={state.data}
							editable={{
								onRowAdd: (newData) =>
									new Promise<void>((resolve) => {
										setTimeout(() => {
											const data = [...state.data];
											data.push(newData);
											setState({ ...state, data });
											resolve();
										}, 600);
									}),
								onRowUpdate: (newData, oldData) =>
									new Promise<void>((resolve) => {
										setTimeout(() => {
											const data = [...state.data];
											if (oldData) data[data.indexOf(oldData)] = newData;
											setState({ ...state, data });
											resolve();
										}, 600);
									}),
								onRowDelete: (oldData) =>
									new Promise<void>((resolve) => {
										setTimeout(() => {
											const data = [...state.data];
											data.splice(data.indexOf(oldData), 1);
											setState({ ...state, data });
											resolve();
										}, 600);
									}),
							}}
						/>
					</div>
				</div>
			</div>
		</div>
	);
};
