import React from 'react';
import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import './style.scss';
import { useState } from 'react';

export const Sidebar = () => {
	const [activeTab, setActiveTab] = useState('');
	const ActiveTab = (tab) => {
		setActiveTab(tab);
	};
	type Anchor = 'top';
	const [state, setState] = React.useState({
		top: false,
	});

	const toggleDrawer = (anchor: Anchor, open: boolean) => (event: React.KeyboardEvent | React.MouseEvent) => {
		if (event.type === 'keydown' && ((event as React.KeyboardEvent).key === 'Tab' || (event as React.KeyboardEvent).key === 'Shift')) {
			return;
		}

		setState({ ...state, [anchor]: open });
	};
	const list = (anchor: Anchor) => (
		<div role='presentation' onClick={toggleDrawer(anchor, false)} onKeyDown={toggleDrawer(anchor, false)}>
			<div className='sider_bar'>
				<div className='sider_bar__title'>
					<h2>TIMLARA</h2>
				</div>
				<div className='sider_bar__content'>
					<div className='sider_bar__content--list'>
						<Button
							variant='contained'
							onClick={() => {
								ActiveTab('overview');
							}}
							className={activeTab === 'overview' ? 'active' : ''}
						>
							<Link to='/'>Tổng quan</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button
							variant='contained'
							onClick={() => {
								ActiveTab('package');
							}}
							className={activeTab === 'package' ? 'active' : ''}
						>
							<Link to='/package'>Gói tin</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button variant='contained'>
							<Link to='/account-information'>Dịch vụ</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button
							variant='contained'
							onClick={() => {
								ActiveTab('advertisement');
							}}
							className={activeTab === 'advertisement' ? 'active' : ''}
						>
							<Link to='/advertisement'>Chiến dịch</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button
							variant='contained'
							onClick={() => {
								ActiveTab('store');
							}}
							className={activeTab === 'store' ? 'active' : ''}
						>
							<Link to='/store'>Cửa hàng</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button variant='contained'>
							<Link to='/account-information'>Tin đăng</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button
							variant='contained'
							onClick={() => {
								ActiveTab('recharge-package');
							}}
							className={activeTab === 'recharge-package' ? 'active' : ''}
						>
							<Link to='/recharge-package'>Gói Nạp Tiền</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button
							variant='contained'
							onClick={() => {
								ActiveTab('account');
							}}
							className={activeTab === 'account' ? 'active' : ''}
						>
							<Link to='/account'>Thành viên</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button variant='contained'>
							<Link to='/account-information'>Thông báo</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button variant='contained'>
							<Link to='/account-information'>Danh mục</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button variant='contained'>
							<Link to='/account-information'>Báo cáo thống kê</Link>
						</Button>
					</div>
					<div className='sider_bar__content--list'>
						<Button
							variant='contained'
							onClick={() => {
								ActiveTab('configuration-management');
							}}
							className={activeTab === 'configuration-management' ? 'active' : ''}
						>
							<Link to='/configuration-management'>Cấu hình</Link>
						</Button>
					</div>
				</div>
			</div>
		</div>
	);

	return (
		<div>
			{/* {(['left', 'right', 'top', 'bottom'] as Anchor[]).map((anchor) => ( */}
			<React.Fragment key={anchor}>
				<Button onClick={toggleDrawer(anchor, true)}>{anchor}</Button>
				<Drawer anchor={anchor} open={state[anchor]} onClose={toggleDrawer(anchor, false)}>
					{list(anchor)}
				</Drawer>
			</React.Fragment>
			{/* ))} */}
		</div>
	);
};
